import styled from '@emotion/styled'
import leftarrow from './assets/leftarrow.svg'
import rightarrow from './assets/rightarrow.svg'

const Container = styled.div`
    background: #FFFFFF;
    border-radius: 4px;
    position: relative;
`

const Diapazon = styled.h3`
    font-family: Jost;
    font-weight: 400;
    font-size: 18px;
    line-height: 26px;
    color: #000000;
    text-align: center;
    padding: 20px 0;
`
const Day = styled.div`
    position: absolute;
    width: 100px;
    height: 16px;
    top: 25px;
    right: 20px;
    display: flex;
    align-content: center;
`

const Arrow = styled.button`
    background: #FFFFFF;
    border: none;
    cursor: pointer;

    :focus {
        outline: none;
    }
`

const Date = styled.span`
    font-weight: 500;
    font-size: 11px;
    line-height: 16px;
    text-transform: uppercase;
    color: #979797;

    :hover {
        text-decoration-line: underline;
    }
`
const Teachers = styled.div`
    text-align: center;
    padding: 20px 0;
`

const Teacher = styled.span`
    font-size: 11px;
    line-height: 16px;
    color: #000000;
`

const Calendar = () => (
    <Container>
        <Diapazon>27 июля – 2 августа 2020 г.</Diapazon>
        <Day>
            <Arrow><img src={leftarrow}/></Arrow>
            <Date>Сегодня</Date>
            <Arrow><img src={rightarrow}/></Arrow>
            <Teachers>
                <Teacher>Высоцкая Элеонора</Teacher>
                <Teacher>Гайсина Гузель</Teacher>
                <Teacher>Гиголо Анна</Teacher>
                <Teacher>Горбукова Юлия</Teacher>
                <Teacher>Марков Руслан</Teacher>
            </Teachers>
        </Day>
    </Container>
)

export default Calendar;
